mapper.select:
select (select user_Id from user_tb where user_pk = b.writter) as boardWritter, b.title as boardTitle, b.content as boardContent 
from board_tb as b
where board_Pk = #{boardId};

mapper.update:
update board_tb 
set title = #{boardTitle}, content = #{boardContent}
where board_Pk = #{boardId};
